import React from 'react';
import { FormControl, InputLabel, Input, Button, Paper, withStyles, CssBaseline, Typography } from '@material-ui/core';
import styles from './styles';
const firebase = require("firebase");

class NewChatComponent extends React.Component{

    constructor(){
        super();
        this.state = {
            username:null,
            message:null
        }
    }

    render(){
        const {classes} = this.props
        return (
           <main className={classes.main}>
               <CssBaseline>
                   <Paper className={classes.paper}>
                       <Typography component="h1" variant="h5">
                            Send A Message
                       </Typography>
                       <form className={classes.form} onSubmit={(e) =>this.submitNewChat(e)}>
                            <FormControl fullWidth>
                                <InputLabel htmlFor="new-chat-username">
                                    Enter Your Friend's Email
                                </InputLabel>
                                <Input required
                                className={classes.input}
                                autoFocus
                                onChange={(e) =>this.userTyping('username',e)}
                                id='new-chat-username'
                                >
                                </Input>
                            </FormControl>
                            <FormControl fullWidth> 
                                <InputLabel htmlFor="new-chat-message">
                                    Enter Your Message
                                </InputLabel>
                            </FormControl>
                       </form>
                   </Paper>
               </CssBaseline>
           </main>
        )
    }
    userTyping = (type,e) =>{
        switch(type) {
            case 'username':
                this.setState({username:e.target.value})
             break;
            case 'message':
                this.setState({message:e.target.value})
             break;
            default:
            break
        }
    }
    submitNewChat = async(e) =>{
        e.preventDefault();
        const userExists=  await this.userExists();
        if(userExists){
            const chatExists = await this.chatExists();
            chatExists ? this.goToChat () :this.createChat();
        }
    }

    buildDocKey = () =>{
        return [firebase.auth().currentUser.email, this.state.username].sort().join(':');
    }

    chatExists = async () =>{
        const docKey = this.buildDocKey(); 
        const chat = await firebase
         .firestore()
         .collection('chats')
         .doc(docKey)
         .get();
         console.log(chat.exists)
         return chat.exists
    }

    userExists = async () =>{
        const userSnapshot = await firebase
         .firestore()
         .collection('users')
         .get();

         const exists = userSnapshot.docs
          .map(_doc => _doc.data().email)
          .includes(this.state.username);

        return exists;
    }
}

export default withStyles(styles)(NewChatComponent);
