import React from "react";
import styles from "./styles";
import { withStyles } from "@material-ui/core/styles";

class ChatViewComponent extends React.Component {
    componentDidMount = () =>{
        const container = document.getElementById('chatview-container');
    }
  render() {
    const { classes, chat,user } = this.props;

    if (chat === undefined) {
      return <main id='chatview-container' className={classes.content}>誰とチャットしたいの？</main>;
      if(container)
      container.scrollTo(0,container.scrollTo)
    } else {
        return (
        <div>
            <div className={classes.chatHeader}>
                あなたは{chat.users.filter(_user => _user !== user)[0]}と会話中です
            </div>
            <main id='chatview-container' className={classes.content}>
                {
                    chat.messages.map((_msg,_index) =>{
                        return(
                            <div key={_index} className={_msg.sender === user ? classes.userSent : classes.friendSent}>
                                {_msg.message}
                            </div>
                        )
                    }

                    )
                }
            </main>
        </div>
        )
    }
  }
}

export default withStyles(styles)(ChatViewComponent);
