import React from 'react';
import ChatList from '../chatList/chatList';
import {Button,withStyles} from '@material-ui/core'
import styles from './style'
const firebase =require('firebase')
class DashbordComponent extends React.Component{
    constructor(){
        super();
        this.state ={
            selectedChat:null,
            newChatFormVisible:false,
            email:null,
            chats:[]
        }
    }
    render(){
        const {classes} = this.props;
return (
    <div>
        Hello world from DashbordComponent
        <ChatList
         history={this.props.history} 
         newChatBtnFn={this.newChatBtnClicked} 
         selectChatFn={this.selectChat}
         chats={this.state.chats}
         userEmail={this.state.email}
         > </ChatList>
         <Button className={classes.signOut} onClick={this.signOut}>Sign Out</Button>
    </div>

)
    }

    selectChat = (chatIndex) =>{
        console.log('Slect', chatIndex)
    }

    newChatBtnClicked = () =>this.setState({newChatFormVisible:true, selectedChat:null})

    componentDidMount = () =>{
        firebase.auth().onAuthStateChanged(async _user =>{
            if(!_user)
            this.props.history.push('/login')
            else{
                await firebase
                 .firestore()
                 .collection('chats')
                 .where('users' , 'array-contains' , _user.email)
                 .onSnapshot(async res =>{
                     const chats = res.docs.map(_doc =>_doc.data());
                     await this.setState({
                         email:_user.email,
                         chats: chats
                     });
                     console.log(this.state)
                 })
            }
        })
    }
}

export default withStyles(styles)(DashbordComponent)
