import React from "react";
import ChatList from "../chatList/chatList";
import { Button, withStyles } from "@material-ui/core";
import styles from "./style";
import ChatViewComponent from '../chatview/chatView'
import ChatTextBox from '../chattextbox/chattextbox';
import NewChatForm from '../newChat/newChat'
const firebase = require("firebase");
class DashbordComponent extends React.Component {
  constructor() {
    super();
    this.state = {
      selectChat: null,
      newChatFormVisible: false,
      email: null,
      chats: []
    };
  }
  render() {
    const { classes } = this.props;
    return (
      <div>
        <ChatList
          history={this.props.history}
          newChatBtnFn={this.newChatBtnClicked}
          selectChatFn={this.selectChat}
          chats={this.state.chats}                 
          userEmail={this.state.email}                 
        >                 
          {" "}                 
        </ChatList>                 
        {                 
            this.state.newChatFormVisible?                  
            null:                 
            <ChatViewComponent                 
                user={this.state.email}                 
                chat={this.state.chats[this.state.selectChat]}                 
            ></ChatViewComponent>                 
        }                 
        {
          this.state.selectChat !== null && !this.state.newChatFormVisible?
          <ChatTextBox　userClickedInputFn={this.messageRead}  submitMessageFn={this.submitMessage}/>:null
        }
        {
          this.state.newChatFormVisible ? <NewChatForm goToChatFn={this.goToChat} newChatSubmitFn={this.newChatSubmit}></NewChatForm>:null
        }
        <Button className={classes.signOutBtn} onClick={this.signOut}>
          Sign Out
        </Button>
      </div>
    );
  }
  signOut= () =>firebase.auth().signOut();

  selectChat = async(chatIndex) => {
   await this.setState({selectChat:chatIndex})
    console.log('セレクトOK')
    this.messageRead()
    console.log('メッセージリードOK')
  };

  submitMessage = (msg) => {
    console.log(1111)
    console.log(this.state.newChatFormVisible)
    console.log(1111)
    const docKey = this.buildDocKey(this.state.chats[this.state.selectChat]
      .users
      .filter(_usr => _usr !== this.state.email)[0])
      console.log(docKey)
    firebase
      .firestore()
      .collection('chats')
      .doc(docKey)
      .update({
        messages: firebase.firestore.FieldValue.arrayUnion({
          message: msg,
          sender: this.state.email
        }),
        receiverHasRead: false
      });
  }
  buildDocKey = (friend) => [this.state.email,friend].sort().join(':');
　goToChat = async (docKey,msg) =>{
  const usersInChat= docKey.split(':')
  const chat = this.state.chats.find(_chat => usersInChat.every(_user =>_chat.users.includes(_user)))
  this.setState({newChatFormVisible :false});
  await this.selectChat(this.state.chats.indexOf(chat))
  this.submitMessage(msg)
}

  newChatSubmit = async (chatObj) =>{
    const docKey = this.buildDocKey(chatObj.sendTo);
    console.log(docKey)
    await firebase
          .firestore()
          .collection('chats')
          .doc(docKey)
          .set({
            receiverHasRead:false,
            users:[this.state.email,chatObj.sendTo],
            message:[{
              message:chatObj.message,
              sender:this.state.email
            }]
          })
          this.setState({newChatFormVisible:false})
          this.selectChat(this.state.chats.length -1)
  }

  clickedMessageWhereNotSender = (chatIndex) => this.state.chats[chatIndex].messages[this.state.chats[chatIndex].messages.length - 1].sender !== this.state.email;
     messageRead = () => {
      const docKey = this.buildDocKey(this.state.chats[this.state.selectChat]
        .users
        .filter(_usr => _usr !== this.state.email)[0])
        console.log(22)
        console.log(docKey)
        console.log(22)
      if(this.clickedMessageWhereNotSender(this.state.selectChat)) {
        firebase
          .firestore()
          .collection('chats')
          .doc(docKey)
          .update({ receiverHasRead: true });
      } else {
        console.log('Clicked message where the user was the sender');
      }
    }

  newChatBtnClicked = () =>
    this.setState({ newChatFormVisible: true, selectChat: null });

    componentWillMount = () => {
      firebase.auth().onAuthStateChanged(async _usr => {
        if(!_usr)
          this.props.history.push('/login');
        else {
          await firebase
            .firestore()
            .collection('chats')
            .where('users', 'array-contains', _usr.email)
            .onSnapshot(async res => {
              const chats = res.docs.map(_doc => _doc.data());
              await this.setState({
                email: _usr.email,
                chats: chats,
                friends: []
              });
            })
        }
    });
  }
}

export default withStyles(styles)(DashbordComponent);
